function sfm_is_valid_extension(filename,extensions)
{
    var found=false;
    var extns = extensions.split(";");
    for(var i=0;i < extns.length;i++)
    {
        var ext_i = $.trim(extns[i]);
        ext = filename.substr(filename.length - ext_i.length,ext_i.length);
        ext = ext.toLowerCase();
        if(ext == extns[i])
        {
            found=true;
            break;
        }
    }
    return found;
}
